package main.java.Sem2;//        Дана json-строка (можно сохранить в файл и читать из файла)//        [{"фамилия":"Иванов","оценка":"5","предмет":"Математика"},{"фамилия":"Петрова","оценка":"4","предмет":"Информатика"},//        {"фамилия":"Краснов","оценка":"5","предмет":"Физика"}]//        Написать метод(ы), который распарсит json и, используя StringBuilder, создаст строки вида: Студент [фамилия]//        получил [оценка] по предмету [предмет].//        Пример вывода://        Студент Иванов получил 5 по предмету Математика.//        Студент Петрова получил 4 по предмету Информатика.//        Студент Краснов получил 5 по предмету Физика.public class Sem2_2_HW {    public static void main(String[] args) {        String str = "[{\"фамилия\":\"Иванов\",\"оценка\":\"5\",\"предмет\":\"Математика\"}," +                "{\"фамилия\":\"Петрова\",\"оценка\":\"4\",\"предмет\":\"Информатика\"}," +                "{\"фамилия\":\"Краснов\",\"оценка\":\"5\",\"предмет\":\"Физика\"}]";        String[] strings = str                .replace("[", "").replace("]", "")                .replace("{", "").replace("}", "")                .replace("\"", "")                .replace(":", " ").replace(",", " ")                .replaceAll("\\s", " ")                .split(" ");        String str1 = "Студент " + strings[1] + " получил " + strings[3] + " по предмету " + strings[5];        String str2 = "Студент " + strings[7] + " получил " + strings[9] + " по предмету " + strings[11];        String str3 = "Студент " + strings[13] + " получил " + strings[15] + " по предмету " + strings[17];        System.out.println(str1);        System.out.println(str2);        System.out.println(str3);    }}